mapper.select:
select tt.* from
(select rownum rn,t.* from
(select e.* from
(select * from LEDEN_COLLECT_PALM order by

    #{overName}


    UPDATE_DATETIME

desc) e


        and RYJCXXCJBH = #{ryjcxxcjbh,jdbcType=VARCHAR}


        and ZHWZHWDM = #{zhwzhwdm,jdbcType=VARCHAR}


        and ZHW_ZZHWQSQKDM = #{zhwZzhwqsqkdm,jdbcType=VARCHAR}


        and ZHW_TXYSFSMS = #{zhwTxysfsms,jdbcType=VARCHAR}


        and ZHW_TXZL = #{zhwTxzl,jdbcType=VARCHAR}


        and DELETAG = #{deletag,jdbcType=VARCHAR}


        and ANNEX = #{annex,jdbcType=VARCHAR}


        and CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}


        and UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}


        and UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP}


        and ZHW_TXSJ = #{zhwTxsj,jdbcType=BLOB}


) t) tt
where rn >= #{startNo} and rn < #{endNo}

select count(1) from LEDEN_COLLECT_PALM


        and RYJCXXCJBH = #{ryjcxxcjbh,jdbcType=VARCHAR}


        and ZHWZHWDM = #{zhwzhwdm,jdbcType=VARCHAR}


        and ZHW_ZZHWQSQKDM = #{zhwZzhwqsqkdm,jdbcType=VARCHAR}


        and ZHW_TXYSFSMS = #{zhwTxysfsms,jdbcType=VARCHAR}


        and ZHW_TXZL = #{zhwTxzl,jdbcType=VARCHAR}


        and DELETAG = #{deletag,jdbcType=VARCHAR}


        and ANNEX = #{annex,jdbcType=VARCHAR}


        and CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}


        and CREATE_DATETIME = #{createDatetime,jdbcType=TIMESTAMP}


        and UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}


        and UPDATE_DATETIME = #{updateDatetime,jdbcType=TIMESTAMP}


        and ZHW_TXSJ = #{zhwTxsj,jdbcType=BLOB}

mapper.delete:
delete from LEDEN_COLLECT_PALM where ryjcxxcjbh=#{personCode}

        and ZHW_TXYSFSMS='0000'


        and ZHW_TXYSFSMS!='0000'

mapper.insert:
begin
insert into LEDEN_COLLECT_PALM (PK_ID, RYJCXXCJBH, ZHWZHWDM, ZHW_ZZHWQSQKDM, ZHW_TXYSFSMS, ZHW_TXZL, DELETAG, ANNEX, CREATE_USER_ID, CREATE_DATETIME, UPDATE_USER_ID, UPDATE_DATETIME, ZHW_TXSJ) values (#{item.pkId,jdbcType=VARCHAR}, #{item.ryjcxxcjbh,jdbcType=VARCHAR}, #{item.zhwzhwdm,jdbcType=VARCHAR}, #{item.zhwZzhwqsqkdm,jdbcType=VARCHAR}, #{item.zhwTxysfsms,jdbcType=VARCHAR}, #{item.zhwTxzl,jdbcType=VARCHAR}, #{item.deletag,jdbcType=VARCHAR}, #{item.annex,jdbcType=VARCHAR}, #{item.createUserId,jdbcType=VARCHAR}, #{item.createDatetime,jdbcType=TIMESTAMP}, #{item.updateUserId,jdbcType=VARCHAR}, #{item.updateDatetime,jdbcType=TIMESTAMP}, #{item.zhwTxsj,jdbcType=BLOB})
;end;
