select tt.* from
(select rownum rn,t.* from
(select p.*,d.NAME,d2.NAME typeName from LEDEN_COLLECT_PERSON p left join SYS_DICT_LIST d on p.AJLBDM = d.code
left join SYS_DICT_LIST d2 on d2.code = p.RYLBDM and d2.sign = 'DICT_RYLB'


        and p.RYJCXXCJBH = #{ryjcxxcjbh,jdbcType=VARCHAR}


        and p.JZRYBH = #{jzrybh,jdbcType=VARCHAR}


        and p.CJXXYYDM = #{cjxxyydm,jdbcType=VARCHAR}


        and p.XM = #{xm,jdbcType=VARCHAR}


        and p.XMHYPY = #{xmhypy,jdbcType=VARCHAR}


        and p.GMSFHM = #{gmsfhm,jdbcType=VARCHAR}


        and p.CYZJDM = #{cyzjdm,jdbcType=VARCHAR}


        and p.ZJHM = #{zjhm,jdbcType=VARCHAR}


        and p.XBDM = #{xbdm,jdbcType=VARCHAR}


        and p.CSRQ = #{csrq,jdbcType=TIMESTAMP}


        and p.CYM = #{cym,jdbcType=VARCHAR}


        and p.WWXM = #{wwxm,jdbcType=VARCHAR}


        and p.BMCH = #{bmch,jdbcType=VARCHAR}


        and p.GJDM = #{gjdm,jdbcType=VARCHAR}


        and p.MZDM = #{mzdm,jdbcType=VARCHAR}


        and p.JGSSXDM = #{jgssxdm,jdbcType=VARCHAR}


        and p.CSDSSXDM = #{csdssxdm,jdbcType=VARCHAR}


        and p.CSDXZ = #{csdxz,jdbcType=VARCHAR}


        and p.HJDSSXDM = #{hjdssxdm,jdbcType=VARCHAR}


        and p.HJDXZ = #{hjdxz,jdbcType=VARCHAR}


        and p.ZZMMDM = #{zzmmdm,jdbcType=VARCHAR}


        and HYZKDM = #{hyzkdm,jdbcType=VARCHAR}


        and XLDM = #{xldm,jdbcType=VARCHAR}


        and GRSFDM = #{grsfdm,jdbcType=VARCHAR}


        and p.RYLBDM = #{rylbdm,jdbcType=VARCHAR}


        and p.XZDQHDM = #{xzdqhdm,jdbcType=VARCHAR}


        and p.XZDXZ = #{xzdxz,jdbcType=VARCHAR}


        and p.AJLBDM = #{ajlbdm,jdbcType=VARCHAR}


        and p.CJRXM = #{cjrxm,jdbcType=VARCHAR}


        and p.CJDWDM = #{cjdwdm,jdbcType=VARCHAR}


        and p.CJDWMC = #{cjdwmc,jdbcType=VARCHAR}


        and p.CJSJ = #{cjsj,jdbcType=TIMESTAMP}


        and p.TSSFDM = #{tssfdm,jdbcType=VARCHAR}


        and p.ZJXYDM = #{zjxydm,jdbcType=VARCHAR}


        and p.COLLECT_CATEGORY_ID = #{collectCategoryId,jdbcType=VARCHAR}


        and p.STATUS = #{status,jdbcType=VARCHAR}


        and p.SCHEDULE = #{schedule,jdbcType=VARCHAR}


        and p.TASK_ID = #{taskId,jdbcType=VARCHAR}


        and p.EQUIPMENT_CODE = #{equipmentCode,jdbcType=VARCHAR}


        and p.DELETAG = #{deletag,jdbcType=VARCHAR}


        and p.ANNEX = #{annex,jdbcType=VARCHAR}


        and p.CREATE_USER_ID = #{createUserId,jdbcType=VARCHAR}


        and p.CREATE_DATETIME >= #{dateStart}


        and p.CREATE_DATETIME <= #{dateEnd}


        and p.UPDATE_USER_ID = #{updateUserId,jdbcType=VARCHAR}


        and p.JDXP = #{jdxp,jdbcType=BLOB}


        and d.sign = #{sign}


order by CJSJ desc
) t) tt
where rn >= #{startNo} and rn < #{endNo}

